function tick2() {
  var e = [750, 751, 752, 753, 754, 755, 756, 757, 758, 759, 760, 761, 762, 763, 764, 765, 766, 767, 768, 769, 770, 771, 772]
    , t = Math.floor(Math.random() * e.length);
  $("#counter1").text(e[t]),
  setTimeout(tick2, Math.round(1e3 + 3e3 * Math.random()))
}

$(document).ready(function() {
  $(".form-1").on('submit', function(e) {
    e.preventDefault();
    $("div#popup").show();
    e.preventDefault()
  });
  
  $("#bottombar1").hide(),
  $("#bottombar1").fadeIn(300),
  tick2(),
  $(".closeit").click(function() {
    $("#bottombar1").fadeOut(300)
  })
})