$(function() {
	$('#phone').on("click keyup change blur", function() {
		if(
		$(this).val().length >= 12 && $(this).val().length <= 12) { $(this).addClass('green').removeClass('red');
		$('#ordbut').removeAttr('disabled', 'disabled'); 
		} else { 
		$(this).removeClass('green').addClass('red');
		$('#ordbut').attr('disabled', 'disabled'); }
	});
	
	$('#phone').bind("change keyup input click", function() {
		if (this.value.match(/[^0-9+]/g)) {
		this.value = this.value.replace(/[^0-9+]/g, '');
		}
		if (this.value.length < 3) {
		this.value = '+48';
		} 
		if (!this.value.match(/^\+48/g)) {
		this.value = '+48';
		}
	});
});    
